    uint256 constant betax2  = 13355670001814658259678411676151414565530086243748149206427327434138641620406;
    uint256 constant betay1  = 13248428204921372864371267617642084278014079614424514927946662671521577072368;
    uint256 constant betay2  = 18864266512698753904143772544862291805377068674199888357328829206547519236407;
